import '../css/App.css';
import { Component } from 'react';
import MobileDevice from './MobileDevice.js'
import { Divider, Stack } from '@mui/material';
import { SketchPicker } from 'react-color';
import rgbHex from 'rgb-hex';

class App extends Component {
  state = {
    mainColor: "#000",
    secondaryColor: "#000",
    accentColor: "#000"
  }

  handleChangeComplete = (color) => {
    console.log(color.hsl.h)
    var accentRgb = (color.hsl.h < 40) 
      ? this.hslToRgb(5, color.hsl.s, color.hsl.l) 
      : this.hslToRgb(color.hsl.h - 40, color.hsl.s, color.hsl.l)
    var secRgb = this.hslToRgb(color.hsl.h, 0.45, 0.97)
    this.setState({
      mainColor: "#"+rgbHex(color.rgb.r, color.rgb.g, color.rgb.b, color.rgb.a),
      secondaryColor: "#"+rgbHex(secRgb[0], secRgb[1], secRgb[2], color.rgb.a),
      accentColor: "#"+rgbHex(accentRgb[0], accentRgb[1], accentRgb[2], color.rgb.a)
    })
  }

  handleChange = (color, event) => {
    var accentRgb = this.hslToRgb(color.hsl.h - 30, color.hsl.s, color.hsl.l)
    var secRgb = this.hslToRgb(color.hsl.h, 0.45, 0.97)
    this.setState({
      mainColor: "#"+rgbHex(color.rgb.r, color.rgb.g, color.rgb.b, color.rgb.a),
      secondaryColor: "#"+rgbHex(secRgb[0], secRgb[1], secRgb[2], color.rgb.a),
      accentColor: "#"+rgbHex(accentRgb[0], accentRgb[1], accentRgb[2], color.rgb.a)
    })
  }

  hslToRgb = (h, s, l) => {
    if (h < 0) {
      h = 0
    }
    let C = (1-Math.abs(2*l - 1))*s
    let X = C*(1-Math.abs((h/60)%2-1))
    let m = l - C/2

    if (0 <= h && h < 60) {
      return [(C+m)*255, (X+m)*255, m*255]
    }
    else if (60 <= h && h<120) {
      return [(X+m)*255, (C+m)*255, m*255]
    }
    else if (120 <= h && h<180) {
      return [m*255, (C+m)*255, (X+m)*255]
    }
    else if (180 <= h && h<240) {
      return [m*255, (X+m)*255, (C+m)*255]
    }
    else if (240 <= h && h<300) {
      return [(X+m)*255, m*255, (C+m)*255]
    }
    else if (300 <= h && h<360) {
      return [(C+m)*255, m*255, (X+m)*255]
    }
  }

  render() {
    let {mainColor, secondaryColor, accentColor} = this.state
    return <div className="App">
      <div className="color-content-body">
            <div className="color-picker-div">
                <div style={{fontSize: "25px"}}>Main Color</div>
                <div style={{width: "220px", display: "inline"}}>
                  <SketchPicker
                    color={mainColor}
                    onChangeComplete={this.handleChangeComplete}
                    onChange={this.handleChange}
                  />
                </div>
            </div>
            <div className="additional-color-div">

            </div>
            <div className="color-scheme-div">

            </div>
        </div>
      <Divider orientation='vertical'></Divider>
      <div className="mobile-device-div">
        <Stack className="mobile-device-stack" direction={"row"} alignItems="center" justifyContent="center" spacing={3}>
          <MobileDevice 
            mainColor={mainColor} 
            secondaryColor={secondaryColor} 
            accentColor={accentColor}
          ></MobileDevice>
          <MobileDevice 
            mainColor={mainColor}
            secondaryColor={accentColor} 
            accentColor={secondaryColor}
          ></MobileDevice>
        </Stack>
      </div>
    </div>
  }
}

export default App;
