import '../css/App.css';
import { Component } from 'react';
import { ChromePicker } from "react-color";
import MobileDevice from './MobileDevice.js'

class App extends Component {
  state = {
    mainColor: "#fff",
    secondaryColor: "#fff",
    accentColor: "#fff"
  }

  render() {
    const {mainColor, secondaryColor, accentColor} = this.state
    return <div className="App">
      <div className="color-content-body">
            <div className="color-picker-div">
                <h3>Main Color</h3>
                <ChromePicker></ChromePicker>
            </div>
            <div className="additional-color-div">

            </div>
            <div className="color-scheme-div">

            </div>
        </div>
      <div className="mobile-device-div">
        <MobileDevice 
          mainColor={this.mainColor} 
          secondaryColor={this.secondaryColor} 
          accentColor={this.accentColor}
        ></MobileDevice>
      </div>
    </div>
  }
}

export default App;
