import '../css/App.css';
import { Component } from 'react';
import MobileDevice from './MobileDevice.js'
import { SketchPicker } from 'react-color';
import rgbHex from 'rgb-hex';

class App extends Component {
  state = {
    mainColor: "#000",
    secondaryColor: "#000",
    accentColor: "#000"
  }

  handleChangeComplete = (color) => {
    console.log(color.hsl)
    this.setState({
      mainColor: "#"+rgbHex(color.rgb.r, color.rgb.g, color.rgb.b, color.rgb.a),
      // secondaryColor: "#"+rgbHex(secColor.rgb.r, secColor.rgb.g, secColor.rgb.b, secColor.rgb.a),
    })
  }

  handleChange = (color, event) => {
    this.setState({
      mainColor: "#"+rgbHex(color.rgb.r, color.rgb.g, color.rgb.b, color.rgb.a),
      accentColor: "#"+rgbHex(color.rgb.r-50, color.rgb.g-50, color.rgb.b-50, color.rgb.a)
    })
  }

  hslRgb = (hsl) => {
    let C = (1-Math.abs(2*hsl.l - 1))*hsl.s
    let X = C*(1-Math.abs((hsl.h/60)%2-1))
    let m = hsl.l - C/2
  }

  render() {
    let {mainColor, secondaryColor, accentColor} = this.state
    return <div className="App">
      <div className="color-content-body">
            <div className="color-picker-div">
                <h2>Main Color</h2>
                <div>
                  <SketchPicker
                    color={mainColor}
                    onChangeComplete={this.handleChangeComplete}
                    onChange={this.handleChange}
                  />
                </div>
            </div>
            <div className="additional-color-div">

            </div>
            <div className="color-scheme-div">

            </div>
        </div>
      <div className="mobile-device-div">
        <MobileDevice 
          mainColor={mainColor} 
          secondaryColor={secondaryColor} 
          accentColor={accentColor}
        ></MobileDevice>
      </div>
    </div>
  }
}

export default App;
