import '../css/App.css';
import { Component } from 'react';
import MobileDevice from './MobileDevice.js'
import { SketchPicker } from 'react-color';
import rgbHex from 'rgb-hex';
import hsl from 'hsl-to-hex';

class App extends Component {
  state = {
    mainColor: "#000",
    secondaryColor: "#000",
    accentColor: "#000"
  }

  handleChangeComplete = (color) => {
    this.setState({
      mainColor: hsl(color.hsl.h, color.hsl.s, color.hsl.l),
      secondaryColor: hsl(color.hsl.h, color.hsl.s, color.hsl.l),
      accentColor: hsl(color.hsl.h, color.hsl.s, color.hsl.l)
    })
  }

  handleChange = (color, event) => {
    this.setState({
      mainColor: hsl(color.hsl.h, color.hsl.s, color.hsl.l),
      secondaryColor: hsl(color.hsl.h, color.hsl.s, color.hsl.l),
      accentColor: hsl(color.hsl.h, color.hsl.s, color.hsl.l)
    })
  }

  render() {
    let {mainColor, secondaryColor, accentColor} = this.state
    return <div className="App">
      <div className="color-content-body">
            <div className="color-picker-div">
                <h2>Main Color</h2>
                <div>
                  <SketchPicker
                    color={mainColor}
                    onChangeComplete={this.handleChangeComplete}
                    onChange={this.handleChange}
                  />
                </div>
            </div>
            <div className="additional-color-div">

            </div>
            <div className="color-scheme-div">

            </div>
        </div>
      <div className="mobile-device-div">
        <MobileDevice 
          mainColor={mainColor} 
          secondaryColor={secondaryColor} 
          accentColor={accentColor}
        ></MobileDevice>
      </div>
    </div>
  }
}

export default App;
