import '../css/App.css';
import { Component } from 'react';
import MobileDevice from './MobileDevice.js'
import { SketchPicker } from 'react-color';
import rgbHex from 'rgb-hex';

class App extends Component {
  state = {
    mainColor: "#000",
    secondaryColor: "#000",
    accentColor: "#000"
  }

  handleChangeComplete = (color) => {
    var accentRgb = this.HSLToRGB(color.hsl.h, color.hsl.s, color.hsl.l)
    this.setState({
      mainColor: "#"+rgbHex(color.rgb.r, color.rgb.g, color.rgb.b, color.rgb.a),
      accentColor: "#"+rgbHex(accentRgb[0], accentRgb[1], accentRgb[2], color.rgb.a)
    })
  }

  handleChange = (color, event) => {
    var accentRgb = this.HSLToRGB(color.hsl.h, color.hsl.s, color.hsl.l)
    this.setState({
      mainColor: "#"+rgbHex(color.rgb.r, color.rgb.g, color.rgb.b, color.rgb.a),
      accentColor: "#"+rgbHex(accentRgb[0], accentRgb[1], accentRgb[2], color.rgb.a)
    })
  }

  HSLToRGB = (h, s, l) => {
    s /= 100;
    l /= 100;
    const k = n => (n + h / 30) % 12;
    const a = s * Math.min(l, 1 - l);
    const f = n =>
      l - a * Math.max(-1, Math.min(k(n) - 3, Math.min(9 - k(n), 1)));
    return [255 * f(0), 255 * f(8), 255 * f(4)];
  }

  render() {
    let {mainColor, secondaryColor, accentColor} = this.state
    return <div className="App">
      <div className="color-content-body">
            <div className="color-picker-div">
                <h2>Main Color</h2>
                <div>
                  <SketchPicker
                    color={mainColor}
                    onChangeComplete={this.handleChangeComplete}
                    onChange={this.handleChange}
                  />
                </div>
            </div>
            <div className="additional-color-div">

            </div>
            <div className="color-scheme-div">

            </div>
        </div>
      <div className="mobile-device-div">
        <MobileDevice 
          mainColor={mainColor} 
          secondaryColor={secondaryColor} 
          accentColor={accentColor}
        ></MobileDevice>
      </div>
    </div>
  }
}

export default App;
